    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
       
        try {
            Class.forName("com.mysql.jdbc.Driver");
         
            Connection conn= DriverManager.getConnection("jdbc:mysql://localhost/hotel","root","admin");
        
            Statement stmt=conn.createStatement();
            
            String sql="insert into book values ("+ T1.getText() + ","+ "'" +T2.getText() +"'" +","+ "'" +T3.getText()+"'" +","+ T4.getText() + ")"+";";
            
            stmt.executeUpdate(sql);
            
            System.out.println("SUCCESFULL");
         }
         catch(Exception e)
         {
              System.out.println("ERROR : " + e);
             
         }
        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
       
         try {
            Class.forName("com.mysql.jdbc.Driver");
         
            Connection conn= DriverManager.getConnection("jdbc:mysql://localhost/hotel","root","admin");
        
            Statement stmt=conn.createStatement();
            
            String sql="update book set room_no="+ T1.getText() + ","+ "room_type="+"'" +T2.getText() +"'" +", bed_type="+ "'" +T3.getText()+"'" +", tarrif="+ T4.getText() + " where room_no="+ T1.getText() +";";
            
            stmt.executeUpdate(sql);
            
            System.out.println("SUCCESFULL");
         }
         catch(Exception e)
         {
              System.out.println("ERROR : " + e);
             
         }
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

