    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        
        try {
            Class.forName("com.mysql.jdbc.Driver");
         
            Connection conn= DriverManager.getConnection("jdbc:mysql://localhost/hotel","root","admin");
        
            Statement stmt=conn.createStatement();
            
            String sql="Select * from book where room_no= "+ s1.getText() +";";
            
            ResultSet rs =stmt.executeQuery(sql);
            
            if(rs.next())
            {
                T1.setText(s1.getText());
                T2.setText(rs.getString("room_type"));
                T3.setText(rs.getString("bed_type"));
                T4.setText(""+rs.getInt("tarrif"));
            }
            
            System.out.println("SUCCESFULL");
            
         }
         catch(Exception e)
         {
              System.out.println("ERROR : " + e);
             
         }
        
    }//GEN-LAST:event_jButton4ActionPerformed

